sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	try {
		data.itemCount = 0;
		data.items = [];
		var gr = new GlideRecordSecure('x_enps_talent_enab_candidate');
		gr.addQuery('system_status','not_reviewed');
		gr.orderByDesc('sys_updated_on');
		gr.query();
		while(gr.next()){
			data.items.push({
				sys_id: gr.sys_id.toString(),
				name: gr.name.toString(),
				lastUpdated: gr.sys_updated_on.toString(),
				state: gr.system_status.getDisplayValue(),
				url: '?id=lf&table=x_enps_talent_enab_candidate&sys_id=' + gr.sys_id.toString()
			});
			data.itemCount++;
		}
	} catch (a) {
		var msg = 'Candidate Watcher TA Input Error: ' + a.toString();
		gs.info(msg);
		gs.addInfoMessage(msg);
	}
})();
